int[0,1] buff_mail[N_V+1]; // election package is delivered to mailbox
int[0,N_C] sh_vt; // chosen candidate
int[0,1] sh_sg; // signed form (honest+private casting)

int ballots_received(){
    int i;
	int res=0;
	if(tally_sum!=0)return tally_sum;
    for(i=1;i<=N_C;i++){
        res+=tally[i];
    }
    return res;
}

